function send(){removeErrors();if(check()){$(".loader").removeClass("hidden");$(".loader").fadeIn();var a=$(this);$.post(a.attr("action"),a.serialize()).then(function(){$("#email").val("");$("#message").val("");$("#alert3").removeClass("hidden");$(".loader").fadeOut()})}}function check(){var a=$("#email").val().trim();var b=$("#message").val().trim();if(a==""||b==""){$("#alert1").removeClass("hidden");return false}return true}function removeErrors(){$("#alert1").addClass("hidden");$("#alert2").addClass("hidden");$("#alert3").addClass("hidden")};