$(document).ready(function(){removeErrors();$(".loader").hide()});function send(){removeErrors();if(check()){var a=$("#email").val().trim();var b=$("#msg").val().trim();$(".loader").fadeIn();$.post("https://robert-voit.de/send_mail",{email:a,msg:b},function(d,c){if(d=="ok"){$("#email").val("");$("#msg").val("");$("#alert3").show()}else{$("#alert2").show()}$(".loader").fadeOut()})}}function check(){var a=$("#email").val().trim();var b=$("#msg").val().trim();if(a==""||b==""){$("#alert1").show();return false}return true}function removeErrors(){$("#alert1").hide();$("#alert2").hide();$("#alert3").hide()};
