function send(){removeErrors();if(check()){var a=$("#email").val().trim();var b=$("#msg").val().trim();$(".loader").removeClass("hidden");$(".loader").fadeIn();$.post("https://robert-voit.de/send_mail",{email:a,msg:b},function(d,c){if(d==="ok"){$("#email").val("");$("#msg").val("");$("#alert3").removeClass("hidden")}else{$("#alert2").removeClass("hidden")}$(".loader").fadeOut()})}}function check(){var a=$("#email").val().trim();var b=$("#msg").val().trim();if(a==""||b==""){$("#alert1").removeClass("hidden");return false}return true}function removeErrors(){$("#alert1").addClass("hidden");$("#alert2").addClass("hidden");$("#alert3").addClass("hidden")};